function t($token) {
    global $translations_nl_en;

    if (!isset($_GET["lang"]) || $_GET["lang"]!="en") {
        return $token;
    }
    $lang="en";
    if ( !isset($translations_nl_en[$token]) ) {
		error_log("WARN: no translation_nl_en for '$token'");
        return $token;
    } else {
        return $translations_nl_en[$token];
    }
}

function l() {
    if (isset($_GET["lang"]) && $_GET["lang"]=="en") {
        return "?lang=en";
    }
}
